/* Local stub for TinyMCE License Key Manager to support GPL/offline dev.
   This provides a minimal API used by TinyMCE core to validate and proceed. */
(function(){
  if (typeof window === 'undefined') return;
  var root = window.tinymce || window.parent && window.parent.tinymce;
  if (!root || typeof root._addLicenseKeyManager !== 'function') return;
  root._addLicenseKeyManager(function(editor){
    return {
      // TinyMCE core calls validate({}) and expects a Promise<boolean>
      validate: function(){ return Promise.resolve(true); }
    };
  });
})();
